<?php

namespace AppBundle\Repository;

use AppBundle\Model\BackendProductSearch\ProductSearchConditions;
use AppBundle\Model\BackendProductSearch\QueryConditionInterface;
use AppBundle\Model\BackendProductSearch\QueryConditionsContainer;
use Doctrine\ORM\EntityRepository;

/**
 * ProductRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class ProductRepository extends EntityRepository
{

    /**
     * @param QueryConditionsContainer|null $queryConditions
     * @return \Doctrine\ORM\Query
     */
    public function getQueryForAdminList(QueryConditionsContainer $queryConditions = null)
    {
        return $this->generateQueryForAdminSearch($queryConditions)->getQuery();
    }

    /**
     * @param QueryConditionsContainer|null $queryConditions
     * @return array
     */
    public function getArrayResultsForAdminList(QueryConditionsContainer $queryConditions = null)
    {
        return $this->generateQueryForAdminSearch($queryConditions)->getQuery()->getArrayResult();
    }

    /**
     * @param QueryConditionsContainer|null $queryConditions
     * @return \Doctrine\ORM\QueryBuilder
     */
    private function generateQueryForAdminSearch(QueryConditionsContainer $queryConditions = null)
    {
        $queryBuilder = $this->getEntityManager()
            ->createQueryBuilder();


        $queryBuilder
            ->select('p.id, 
            p.name, 
            p.isVisible, 
            p.isAvailable, 
            p.onStockAmount, 
            p.price.amount AS price, 
            p.price.currency AS currency,
            p.introduction, 
            p.description')
            ->from('AppBundle:Product', 'p');

        if (!is_null($queryConditions) && $queryConditions->hasQueryConditions()) {
            $andX = $queryBuilder->expr()->andX();
            foreach ($queryConditions->getQueryConditions() as $queryCondition) {
                $andX->add($queryCondition->getQueryExpression());
                $parameter = $queryCondition->getParameter();
                $queryBuilder->setParameter($parameter->getParameterAlias(), $parameter->getParameterValue());
            }
            $queryBuilder->add('where', $andX);
        };

        return $queryBuilder;
    }

    /**
     * @return \Doctrine\ORM\Query
     */
    public function getQueryForCustomerHomepage()
    {
        return $this->generateQueryForCustomerHomepage()->getQuery();
    }

    /**
     * @return array
     */
    public function getArrayResultForCustomerHomepage()
    {
        return $this->generateQueryForCustomerHomepage()->getQuery()->getArrayResult();
    }

    /**
     * @return \Doctrine\ORM\QueryBuilder
     */
    private function generateQueryForCustomerHomepage()
    {
        $queryBuilder = $this->getEntityManager()
            ->createQueryBuilder();

        $queryBuilder->select('p.id, 
            p.name,
            p.onStockAmount, 
            p.price.amount AS price, 
            p.price.currency AS currency,
            p.introduction, 
            p.description'
        )->from('AppBundle:Product', 'p')
            ->where('p.isVisible = :isVisible')
            ->andWhere('p.isAvailable = :isAvailable');

        return $queryBuilder;
    }
}
